mapper.insert:

 		SELECT ADDITIONAL_FACILITY_INFO_SEQ.NEXTVAL FROM DUAL

      INSERT INTO ADDITIONAL_FACILITY_INFO(
			    ADD_NO
			   ,ADD_CTG_NO
			   ,ADD_NAME
			   ,ADD_LOCATION_INFO
			   ,ADD_CONTENT
			   ,ADD_PRICE
			   ,APT_NO
			   ,ADDINFO_IMG_FILENAME
			   ,ADDINFO_IMG_ORIGINAL_FILENAME

			   )
	  VALUES(
		       	#{addNo}
		       ,#{addCtgNo}
		       ,#{addName}
		       ,#{addLocationInfo}
		       ,#{addContent}
		       ,#{addPrice}
		       ,#{aptNo}

		       ,DEFAULT
		       ,DEFAULT


		       ,#{addinfoImgFilename}
		       ,#{addinfoImgOriginalFilename}

	  )

mapper.select:
SELECT COUNT(*) FROM ADDITIONAL_FACILITY_INFO I JOIN ADD_CATEGORY C ON I.ADD_CTG_NO= C.ADD_CTG_NO WHERE APT_NO = (SELECT APT_NO FROM HOUSEHOLD_INFO WHERE HOUSEHOLD_CODE = #{householdCode})
AND ${searchCondition} like '%' || #{searchKeyword} || '%'
AND ADD_CTG_NO = #{addCtgNo}

SELECT COUNT(*) 
  FROM ADD_ORDER_LIST
 WHERE HOUSEHOLD_CODE=#{householdCode}
   AND ADD_NO=#{addNo} 
   AND ADD_OUTDATE IS NULL

mapper.insert:

 	SELECT ADD_ORDER_LIST_SEQ.NEXTVAL FROM DUAL

	INSERT INTO ADD_ORDER_LIST(
	     ADD_ORDER_LIST_NO
	    ,ADD_NO
	    ,HOUSEHOLD_CODE
	    ,MEMBER_NO
	)
	VALUES(
		 #{addOrderListNo}    
	    ,#{addNo}
	    ,#{householdCode}
	    ,#{memberNo}
	)

mapper.update:
UPDATE ADD_ORDER_LIST 
   SET ADD_OUTDATE = SYSDATE
 WHERE ADD_ORDER_LIST_NO = #{addOrderListNo}
