mapper.select:
SELECT I.U_COST_NO ,I.U_COST_AMOUNT ,I.U_COST_PRICE ,I.HOUSEHOLD_CODE ,I.U_CTG_NO ,(SELECT U_CTG_NAME FROM UTILITY_COST_CTG C WHERE C.U_CTG_NO=I.U_CTG_NO) AS U_CTG_NAME ,TO_CHAR(I.U_COST_USEDATE,'MM') AS USE_MONTH ,H.DONG ,H.HO ,H.APT_NO ,(SELECT APT_NAME FROM APARTMENT_INFO A WHERE A.APT_NO = H.APT_NO) AS APT_NAME FROM UTILITY_COST_INFO I JOIN HOUSEHOLD_INFO H ON H.HOUSEHOLD_CODE = I.HOUSEHOLD_CODE WHERE I.U_CTG_NO= 3 AND I.HOUSEHOLD_CODE = #{householdInfo}
AND I.U_COST_USEDATE > TO_DATE(TO_CHAR(ADD_MONTHS(SYSDATE,-12),'YYYY-MM'),'YYYY-MM')
ORDER BY U_COST_USEDATE

SELECT I.U_COST_NO ,I.U_COST_AMOUNT ,I.U_COST_PRICE ,I.HOUSEHOLD_CODE ,I.U_CTG_NO ,(SELECT U_CTG_NAME FROM UTILITY_COST_CTG C WHERE C.U_CTG_NO=I.U_CTG_NO) AS U_CTG_NAME ,TO_CHAR(I.U_COST_USEDATE,'MM') AS USE_MONTH ,H.DONG ,H.HO ,H.APT_NO ,(SELECT APT_NAME FROM APARTMENT_INFO A WHERE A.APT_NO = H.APT_NO) AS APT_NAME FROM UTILITY_COST_INFO I JOIN HOUSEHOLD_INFO H ON H.HOUSEHOLD_CODE = I.HOUSEHOLD_CODE WHERE I.U_CTG_NO=#{uCtgNo} AND I.HOUSEHOLD_CODE = #{householdInfo}
AND I.U_COST_USEDATE > TO_DATE(TO_CHAR(ADD_MONTHS(SYSDATE,-12),'YYYY-MM'),'YYYY-MM')
ORDER BY U_COST_USEDATE
