mapper.select:
SELECT * FROM MNGCOST_INFO WHERE APT_NO=#{aptNo}
AND MNGCOST_USEDATE BETWEEN #{startdate} AND TO_DATE(#{enddate})+1
AND TO_CHAR(MNGCOST_USEDATE, 'YYYY-MM') =TO_CHAR(SYSDATE, 'YYYY-MM')
AND MNGCOST_S_CTG_NO=#{mngcostSCtgNo}
AND MNGCOST_S_CTG_NO IN (SELECT MNGCOST_S_CTG_NO FROM MNGCOST_SUB_CTG WHERE MNGCOST_M_CTG_NO=#{mngcostMCtgNo})
ORDER BY MNGCOST_NO DESC

mapper.update:
UPDATE MNGCOST_INFO
SET MNGCOST_S_CTG_NO=#{mngcostSCtgNo},
	MNGCOST_CONTENT=#{mngcostContent},
	MNGCOST_PRICE=#{mngcostPrice}
WHERE MNGCOST_NO=#{mngcostNo}

mapper.select:
SELECT MNGCOST_LIST_NO, DONG, HO, MNGCOST_TOTAL_PRICE, MNGCOST_CLAIMDATE, MNGCOST_LIMITDATE, MNGCOST_PAYDATE FROM MNGCOST_PAYMENT_LIST L JOIN HOUSEHOLD_INFO I ON L.HOUSEHOLD_CODE = I.HOUSEHOLD_CODE WHERE L.HOUSEHOLD_CODE IN (SELECT HOUSEHOLD_CODE FROM HOUSEHOLD_INFO WHERE APT_NO=#{aptNo} AND AUTH_CODE='RESIDENT')
AND DONG=#{dong}
AND HO=#{ho}
AND MNGCOST_PAYDATE IS NULL
ORDER BY MNGCOST_CLAIMDATE DESC
