select NOTICE_BOARD_SEQ.nextval from dual

INSERT INTO NOTICE_BOARD(
    NOTICE_NO,
    N_CTG_NO,
    NOTICE_TITLE,
    NOTICE_CONTENT,
    NOTICE_REGDATE,
    MEMBER_NO,
    APT_NO
)
SELECT 
	 #{noticeNo}
	,#{nCtgNo}
    ,#{noticeTitle}
    ,#{noticeContent}
    ,SYSDATE
    ,#{memberNo}
    ,APT_NO
 FROM 
 	HOUSEHOLD_INFO 
 WHERE 
 	HOUSEHOLD_CODE = #{householdCode}

mapper.update:
UPDATE NOTICE_BOARD
   SET NOTICE_READCOUNT=NOTICE_READCOUNT+1 
 WHERE NOTICE_NO=#{noticeNo}

		UPDATE 
			 NOTICE_BOARD
  		SET  
  			 N_CTG_NO=#{nCtgNo}
			,NOTICE_TITLE=#{noticeTitle}
			,NOTICE_CONTENT=#{noticeContent}
 		WHERE 
 			 NOTICE_NO=#{noticeNo}

mapper.insert:

	select SCHEDULER_SEQ.NEXTVAL from dual 	

insert into SCHEDULER
(
    SCD_NO
   ,SCD_CONTENT
   ,SCD_S_DATE
   ,SCD_E_DATE
   ,NOTICE_NO
   ,APT_NO
)
SELECT
    #{scdNo}
   ,#{scdContent}
   ,#{startDate}
   ,#{endDate}
   ,#{noticeNo}
   ,APT_NO
FROM 
 	HOUSEHOLD_INFO 
WHERE 
 	HOUSEHOLD_CODE = #{householdCode}

mapper.update:
	UPDATE SCHEDULER
       SET  
         SCD_S_DATE  = #{startDate}
	    ,SCD_E_DATE  = #{endDate}
	    ,SCD_CONTENT = #{scdContent}
     WHERE SCD_NO = #{scdNo}

mapper.insert:

	SELECT NOTICE_STORAGE_SEQ.NEXTVAL FROM DUAL

INSERT INTO NOTICE_STORAGE(
     N_STORAGE_NO
	,NOTICE_NO
	,NOTICE_FILENAME
	,NOTICE_ORIGINAL_FILENAME
	,NOTICE_FILESIZE 
)VALUES(
     #{nStorageNo}
	,#{noticeNo}
	,#{noticeFilename}
	,#{noticeOriginalFilename}
	,#{noticeFilesize}
)

mapper.update:
UPDATE NOTICE_STORAGE
   SET 
	 NOTICE_FILENAME= #{noticeFilename}
	,NOTICE_ORIGINAL_FILENAME= #{noticeOriginalFilename}
	,NOTICE_FILESIZE = #{noticeFilesize}
WHERE NOTICE_NO = #{noticeNo}
